package com.example.news2.ui.details;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.databinding.DataBindingUtil;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import com.example.news2.R;
import com.example.news2.data.model.BreakingNewsModel;
import com.example.news2.data.model.CategorizedNewsModel;
import com.example.news2.databinding.FragmentCategorizedNewsBinding;
import com.example.news2.ui.adapter.CategorizedNewsAdapter;

import java.util.ArrayList;
import java.util.List;

public class CategorizedNewsFragment extends Fragment {

    private FragmentCategorizedNewsBinding binding;

    RecyclerView recyclerView;
    CategorizedNewsAdapter adapter;
    List<CategorizedNewsModel> news;

    int type;


    @Nullable
    @Override
    public View onCreateView(@NonNull LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        binding = DataBindingUtil.inflate(inflater, R.layout.fragment_categorized_news, container, false);

        if (getArguments() != null) {
            type = getArguments().getInt("type");
        }

        init();

        return binding.getRoot();
    }

    private void init() {
        recyclerView = binding.recyclerView;
        news = new ArrayList<>();
        adapter = new CategorizedNewsAdapter(news, getContext());
        recyclerView.setLayoutManager(new LinearLayoutManager(getContext()));
        recyclerView.setHasFixedSize(true);
        recyclerView.setAdapter(adapter);


        switch (type) {
            case 1:
                binding.txtCategory.setText("POLITICS");
                getNews(1);
                break;
            case 2:
                binding.txtCategory.setText("SPORTS");
                getNews(2);
                break;
            case 3:
                binding.txtCategory.setText("ECONOMY");
                getNews(3);
                break;
            default:
                binding.txtCategory.setText("POLITICS");
                getNews(1);
                break;
        }
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
    }

    private void getNews(int type){

        news.add(new CategorizedNewsModel("Politics","Ukrainian ex-top official shot dead outside Madrid school","data:image/jpeg;base64,"));
        news.add(new CategorizedNewsModel("Celebrity","Jury shown guns and drugs seized at Diddy's mansion","data:image/jpeg;base64,"));
        news.add(new CategorizedNewsModel("Sports","How do you prepare for a cup final of this magnitude?","data:image/png;base64,"));

        adapter.notifyDataSetChanged();
    }
}
